import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  data() {
    return {
      answer: {
        answerText: '',
        aImage: null // 기존 이미지 URL
      },
      previewImageSrc: null // 미리보기 이미지
    };
  },
  created() {
    this.fetchAnswerDetail();
  },
  methods: {
    async fetchAnswerDetail() {
      const questionId = this.$route.params.id;
      try {
        const response = await axios.get(`${process.env.VUE_APP_API_BASE_URL}/qna/detail/${questionId}`);
        this.answer = response.data.result;

        // 기존 이미지가 있는 경우 previewImageSrc에 설정
        if (this.answer.aImage) {
          this.previewImageSrc = this.getExistingImageUrl();
        }
      } catch (error) {
        this.$router.push('/qna/list');
      }
    },
    getExistingImageUrl() {
      // 기존 이미지의 절대 경로를 반환하는 함수
      console.log(`${process.env.VUE_APP_API_BASE_URL}/${this.answer.aImage}`);
      return `${process.env.VUE_APP_API_BASE_URL}/${this.answer.aImage}`;
    },
    onFileChange(event) {
      const files = event?.target?.files || event?.dataTransfer?.files;
      if (files && files.length > 0) {
        this.previewImageSrc = null; // 새 이미지 선택 시 기존 미리보기 초기화
        this.previewImage(files[0]); // 새 이미지로 미리보기 업데이트
      } else {
        this.answer.aImage = null;
        this.previewImageSrc = null;
      }
    },
    previewImage(file) {
      const reader = new FileReader();
      reader.onload = e => {
        this.previewImageSrc = e.target.result;
      };
      reader.readAsDataURL(file);
    },
    async updateAnswer() {
      const formData = new FormData();
      formData.append('answerText', this.answer.answerText);

      // 새 이미지가 선택된 경우에만 FormData에 추가
      if (this.previewImageSrc && this.previewImageSrc !== this.getExistingImageUrl()) {
        formData.append('image', this.answer.aImage);
      }
      try {
        await axios.post(`${process.env.VUE_APP_API_BASE_URL}/qna/update/answer/${this.$route.params.id}`, formData);
        this.$router.push(`/qna/detail/${this.$route.params.id}`);
      } catch (error) {
        alert('답변 수정에 실패했습니다.');
      }
    },
    goBack() {
      const questionId = this.$route.params.id;
      this.$router.push(`/qna/detail/${questionId}`);
    }
  }
};
