import axios from 'axios';
export default {
  name: "PasswordResetPage",
  data() {
    return {
      name: "",
      email: "",
      phone: "",
      errorMessage: "",
      successMessage: ""
    };
  },
  methods: {
    async sendResetLink() {
      if (!this.name || !this.email || !this.phone) {
        alert('모든 필드를 입력해주세요.');
        return;
      }

      // JWT 토큰을 가져오는 로직이 필요함 (예: 로컬 스토리지 또는 Vuex에서)
      const token = localStorage.getItem('token');
      console.log(token);
      try {
        const response = await axios.post(`${process.env.VUE_APP_API_BASE_URL}/find-password`, {
          name: this.name,
          phone: this.phone,
          email: this.email
        });
        this.successMessage = '비밀번호 재설정 링크가 이메일로 발송되었습니다.';
        this.errorMessage = "";
        console.log(response);
      } catch (e) {
        console.log(e.response);
        this.errorMessage = e.response?.data?.status_message || '알 수 없는 오류가 발생했습니다.';
        console.log(this.errorMessage);
        this.successMessage = "";
      }
    }
  }
};
