import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  data() {
    return {
      formData: {
        title: '',
        questionText: '',
        qImage: null
      },
      previewImageSrc: null
    };
  },
  methods: {
    handleFileChange(files) {
      if (files && files.length > 0) {
        this.formData.qImage = files[0];
        this.previewImage();
      } else {
        this.formData.qImage = null;
        this.previewImageSrc = null;
      }
    },
    previewImage() {
      if (this.formData.qImage) {
        const reader = new FileReader();
        reader.onload = e => {
          this.previewImageSrc = e.target.result;
        };
        reader.readAsDataURL(this.formData.qImage);
      } else {
        this.previewImageSrc = null;
      }
    },
    async submitForm() {
      const formData = new FormData();
      formData.append('title', this.formData.title);
      formData.append('questionText', this.formData.questionText);
      if (this.formData.qImage) {
        formData.append('image', this.formData.qImage);
      }
      try {
        const response = await axios.post(`${process.env.VUE_APP_API_BASE_URL}/qna/create`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        console.log(response.data);
        alert('Question created successfully!');
        this.resetForm();
        this.$router.push({
          name: 'QnaList'
        });
      } catch (error) {
        const errorMessage = error.response && error.response.data ? error.response.data.message : 'Failed to create question';
        alert(errorMessage);
        console.error('Full error details:', error);
      }
    },
    resetForm() {
      this.formData.title = '';
      this.formData.questionText = '';
      this.formData.qImage = null;
      this.previewImageSrc = null;
      if (this.$refs.form) {
        this.$refs.form.reset();
      }
    }
  }
};
