import axios from 'axios';
export default {
  name: "PasswordResetPage",
  data() {
    return {
      email: ""
    };
  },
  methods: {
    async sendResetLink() {
      if (!this.email) {
        alert('이메일을 입력해주세요.');
        return;
      }

      // JWT 토큰을 가져오는 로직이 필요함 (예: 로컬 스토리지 또는 Vuex에서)
      const token = localStorage.getItem('jwt_token');
      try {
        const response = await axios.post(`${process.env.VUE_APP_API_BASE_URL}/user/reset-password`, {
          email: this.email
        }, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        alert('비밀번호 재설정 링크가 이메일로 발송되었습니다.');
        console.log(response);
      } catch (e) {
        console.log(e.response);
        alert('비밀번호 재설정 요청에 실패하였습니다.');
        const error_message = e.response?.data?.status_message || '알 수 없는 오류가 발생했습니다.';
        alert(error_message);
      }
    }
  }
};
