import axios from 'axios';
export default {
  name: "PasswordResetPage",
  data() {
    return {
      name: "",
      email: "",
      phone: "",
      errorMessage: "",
      successMessage: ""
    };
  },
  methods: {
    async sendResetLink() {
      if (!this.name || !this.email || !this.phone) {
        alert('모든 필드를 입력해주세요.');
        return;
      }
      try {
        const response = await axios.post(`${process.env.VUE_APP_API_BASE_URL}/user/find-password`, {
          name: this.name,
          phone: this.phone,
          email: this.email
        });
        this.successMessage = '비밀번호 재설정 링크가 이메일로 발송되었습니다.';
        alert(this.successMessage); // 창으로 성공 메시지 표시
        this.errorMessage = "";
        console.log(response);
      } catch (e) {
        console.log(e.response);
        this.errorMessage = e.response?.data?.status_message || '알 수 없는 오류가 발생했습니다.';
        console.log(this.errorMessage);
        alert(this.errorMessage); // 창으로 오류 메시지 표시
        this.successMessage = "";
      }
    }
  }
};
